mapper.select:
select a.id, a.masterplateid, a.count, a.masterplatenum,name,b.masterplateName masterplatename from vod_masterplate_son a left join vod_masterplate b on a.masterplateid=b.id
and a.name like '%${name}%'
and a.masterplateid = #{masterplateid,jdbcType=INTEGER}
order by a.masterplateid,a.masterplatenum
limit #{start},#{pageSize}

select a.id, a.masterplateid, a.count, a.masterplatenum,name,b.masterplateName masterplatename from vod_masterplate_son a left join vod_masterplate b on a.masterplateid=b.id
and a.name like '%${name}%'
and a.masterplateid = #{masterplateid,jdbcType=INTEGER}
order by a.masterplateid,a.masterplatenum
limit #{start},#{pageSize}

mapper.delete:
delete from vod_masterplate_son
where 1=1

	and id = #{id,jdbcType=INTEGER}
