mapper.select:
        select 
         channelCode AS ID,
         channelName  AS NAME 
        from  vod_phone_channel 
        where 1=1

		            and ID = #{id,jdbcType=INTEGER}


		            and levels = #{levels,jdbcType=INTEGER}


		            and channelCode = #{channelCode,jdbcType=INTEGER}


		            and parentCode = #{parentCode,jdbcType=INTEGER}


		            and navigationtype = #{navigationtype,jdbcType=INTEGER}


		            and status = #{status,jdbcType=INTEGER}

        ORDER BY SEQUENCE

	       select id,name,type,status from vod_phone_label
	       where 1=1

            and ID = #{id,jdbcType=INTEGER}


		            and type = #{type,jdbcType=INTEGER}


            and status = #{status,jdbcType=INTEGER}


            and id in( select label from vod_labelchannel 
            where type='phone' and  channel= #{channelCode,jdbcType=INTEGER})

        ORDER BY SEQUENCE

        select 
         channelCode AS ID,
         channelName  AS NAME 
        from  vod_tv_channel 
        where 1=1

		            and ID = #{id,jdbcType=INTEGER}


		            and levels = #{levels,jdbcType=INTEGER}


		            and channelCode = #{channelCode,jdbcType=INTEGER}


		            and parentCode = #{parentCode,jdbcType=INTEGER}


		            and navigationtype = #{navigationtype,jdbcType=INTEGER}


		            and status = #{status,jdbcType=INTEGER}

        ORDER BY SEQUENCE

	       select id,name,type,status from vod_phone_label


		            and ID = #{id,jdbcType=INTEGER}


		            and type = #{type,jdbcType=INTEGER}


		            and status = #{status,jdbcType=INTEGER}


		            and id in( select label from vod_labelchannel where 
		            type='tv' and channel= #{channelCode,jdbcType=INTEGER})


        ORDER BY SEQUENCE

select b.channelCode as ID, b.channelName AS NAME from vod_buss_channel a left join vod_tv_channel b on a.channelCode=b.channelCode where levels =1
and bussId = #{bussId,jdbcType=INTEGER}
and groupId = #{groupId,jdbcType=INTEGER}

select a.channelCode as ID, a.channelName AS NAME from vod_tv_channel a where levels =1 and channelCode not in( select channelCode from vod_buss_channel where 1=1
and bussId = #{bussId,jdbcType=INTEGER}
and groupId = #{groupId,jdbcType=INTEGER}
)
