mapper.select:
   select id,name,channelCode,count,status,type,update_time,update_user,create_time,create_user from vod_phone_hotseries
   where 1=1

     and id=#{id,jdbcType=INTEGER}


     and name=#{name,jdbcType=VARCHAR}


     and channelCode=#{channelCode,jdbcType=INTEGER}


     and status=#{status,jdbcType=INTEGER}


     and type=#{type,jdbcType=INTEGER}


			and
			(date_format(create_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
			or date_format(update_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
		    )

   select id,name,channelCode,count,status,type from vod_phone_hotseries
   where 1=1

     and id=#{id,jdbcType=INTEGER}


     and name=#{name,jdbcType=VARCHAR}


     and channelCode=#{channelCode,jdbcType=INTEGER}


     and status=#{status,jdbcType=INTEGER}


     and type=#{type,jdbcType=INTEGER}


			and
			(date_format(create_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
			or date_format(update_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
		    )

    select count(id) from vod_phone_hotseries
    where 1=1

     and id=#{id,jdbcType=INTEGER}


     and name=#{name,jdbcType=VARCHAR}


     and channelCode=#{channelCode,jdbcType=INTEGER}


     and status=#{status,jdbcType=INTEGER}


     and type=#{type,jdbcType=INTEGER}


			and
			(date_format(create_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
			or date_format(update_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
		    )

mapper.update:
    update vod_phone_hotseries set status=#{status,jdbcType=INTEGER}
    where 1=1

	    and id=#{id,jdbcType=INTEGER}

mapper.delete:
     delete from vod_phone_hotseries


	       and id=#{id,jdbcType=INTEGER}


		     and id in

				#{item,jdbcType=INTEGER}

mapper.select:
    select hot_id,seriesCode,sequence from vod_phone_hotseriesvideo
    where 1=1

			     and hot_id=#{hot_id,jdbcType=INTEGER}

	  order by 	sequence

            limit #{start},#{pageSize}

select a.seriesCode,b.seriesName,b.ChannelName,b.actorName, writerName,b.volumncount,b.currentnum,b.status,a.sequence from vod_phone_hotseriesvideo a left join vod_phone_albuminfo b on a.seriesCode=b.seriesCode where 1=1
and a.hot_id=#{hot_id,jdbcType=INTEGER}
order by a.sequence
limit #{start},#{pageSize}

select count(hot_id) from vod_phone_hotseriesvideo
where 1=1

  and  hot_id=#{hot_id,jdbcType=INTEGER}


  and  seriesCode=#{seriesCode,jdbcType=INTEGER}


     limit #{start},#{pageSize}

mapper.delete:
       delete from vod_phone_hotseriesvideo 


	       and hot_id=#{hot_id,jdbcType=INTEGER}


            and seriesCode=#{seriesCode,jdbcType=INTEGER}


		     and hot_id in

				#{item,jdbcType=INTEGER}

mapper.select:
select max(sequence) max, min(sequence) min from vod_phone_hotseriesvideo a
and a.hot_id=#{hot_id,jdbcType=INTEGER}
and a.seriesCode in (${albumid})

select a.seriesCode,a.sequence
from vod_phone_hotseriesvideo a


		and a.hot_id=#{hot_id,jdbcType=INTEGER}


		and a.seriesCode not in (${albumid})


	       	    and sequence<= #{max,jdbcType=INTEGER}


	            and sequence>= #{min,jdbcType=INTEGER}


order by a.sequence
