mapper.select:
		select 
     id, name	,imgurl	,backgroundimg,viewPoint	,describes,
	  status	,sequence	,web_url	,type,template,textcolor
       from  vod_tv_special a  
       where 1=1        
           and date_format(create_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}
           or date_format(update_time,'%Y-%m-%d') = #{create_time,jdbcType=DATE}



           limit #{start},#{pageSize}

select
from vod_tv_special a where 1=1
limit #{start},#{pageSize}

mapper.update:
update vod_tv_special set
 status = #{status,jdbcType=INTEGER}
 where id=#{id,jdbcType=INTEGER}

			update vod_tv_special set 


			    sequence=sequence+1


			    sequence=sequence-1


    		where  1=1

		        	and id !=#{id,jdbcType=INTEGER} 


    		  and sequence>=#{minnum,jdbcType=INTEGER}
    		  and sequence<=#{maxnum,jdbcType=INTEGER}

mapper.delete:
        delete from vod_tv_special_albuminfo
         where 1=1 

			     and seriesCode=#{seriesCode,jdbcType=INTEGER}


			     and  special_id=#{special_id,jdbcType=INTEGER}


			      and id=#{id,jdbcType=INTEGER}

mapper.update:
update vod_tv_special_albuminfo set
sequence=#{sequence,jdbcType=INTEGER}
 where special_id=#{special_id,jdbcType=INTEGER}
 and seriesCode =#{seriesCode,jdbcType=INTEGER}

mapper.select:
select count(ID) from vod_tv_special_albuminfo a left join vod_phone_albuminfo b on a.seriesCode=b.seriesCode where 1=1
and a.seriesCode in(${codelist})
and b.status=1 and b.Channel in( select channelCode name from vod_buss_channel where bussId in (select bussId from vod_buss_info where name=#{busschannel,jdbcType=VARCHAR}) )
and a.special_id=#{special_id,jdbcType=INTEGER}
and a.seriesCode=#{seriesCode,jdbcType=INTEGER}
and b.seriesName like '%${seriesName}%'
and b.status=#{status,jdbcType=INTEGER}

 			select  a.seriesCode,b.seriesName,b.ChannelName,b.actorName,
 			writerName,b.volumncount,b.currentnum,b.status,a.sequence
		     from  vod_tv_special_albuminfo a
		   left join vod_phone_albuminfo b on  a.seriesCode=b.seriesCode


		         and a.special_id=#{special_id,jdbcType=INTEGER}


		          and a.seriesCode=#{seriesCode,jdbcType=INTEGER}


		     		and b.seriesName like '%${seriesName}%'


			     and b.status=#{status,jdbcType=INTEGER}


         	order by 	a.sequence

            limit #{start},#{pageSize}

select max(sequence) max, min(sequence) min from vod_tv_special_albuminfo a
and a.special_id=#{special_id,jdbcType=INTEGER}
and a.seriesCode in (${albumid})

select a.seriesCode,a.sequence
from vod_tv_special_albuminfo a


		and a.special_id=#{special_id,jdbcType=INTEGER}


		and a.seriesCode not in (${albumid})


	       	    and sequence<= #{max,jdbcType=INTEGER}


	            and sequence>= #{min,jdbcType=INTEGER}


order by a.sequence

select a.id,a.special_id,a.seriesCode,a.sequence from vod_tv_special_albuminfo a left join vod_phone_albuminfo b on a.seriesCode=b.seriesCode where 1=1
and b.status=1 and b.Channel in( select channelCode name from vod_buss_channel where bussId in (select bussId from vod_buss_info where name=#{busschannel,jdbcType=VARCHAR}) )
and special_id=#{special_id,jdbcType=INTEGER}
and seriesCode in(${codelist})
order by sequence limit #{start},#{pageSize}

select a.id,a.sequence
from vod_tv_special a


		and a.id not in (${id})


	       	    and sequence<= #{max,jdbcType=INTEGER}


	            and sequence>= #{min,jdbcType=INTEGER}


order by a.sequence
